bool isValidExpression(std::string s) {
  std::stack<char> st;

  for (char c : s) {
    if (c == '(' || c == '{' || c == '[') {
      st.push(c);
    } else if (c == ')' || c == '}' || c == ']') {
      if (st.empty()) {
        return false;
      } else if ((c == ')' && st.top() == '(') ||
                 (c == '}' && st.top() == '{') ||
                 (c == ']' && st.top() == '[')) {
        st.pop();
      } else {
        return false;
      }
    }
  }

  return st.empty();
}

